import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import CryptoJS from "crypto-js";
import Comic from './Components/comic';
import Page from './Components/paginate';
import {Container,Row,Button, Navbar,
  Collapse,
  Nav,
  NavItem,
  NavLink,NavbarToggler }from 'reactstrap'
// import {Provider} from 'react-redux';
// import {createStore, combineReducers} from 'redux';
// import Data from './Components/Reduceur/nbPage.reduceur'
// import {connect} from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
const PRIV_KEY = "ce1693d8baa06f879abcab4c75c19e34006074db";
const PUBLIC_KEY = "1415ee478404a80e6f620f034e526ca0";
const ts = new Date().getTime();
const hash = CryptoJS.MD5(ts + PRIV_KEY + PUBLIC_KEY).toString();


// const store = createStore(combineReducers({Data}));

class App extends Component {
  state = {
    data: [],
    comicCount: 0,
    comicNameList: [],
    limit : 8,
    viewOnlyLike: false,
    offset : 0
  }

  componentDidMount() {
    fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=${this.state.offset}&limit=${this.state.limit}&apikey=${PUBLIC_KEY}&hash=${hash}`)
    .then(response => response.json())
    .then(data => this.setState({data: data.data.results}));
  }
  handleClickLikeOn=()=> {
    console.log("I only want to see the liked films")
    this.setState({
      viewOnlyLike: true
    })
  }


  hundleClick=()=>{
    this.setState({
    limit : 8 , offset : 0
    })
    fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=${this.state.offset}&limit=${this.state.limit}&apikey=${PUBLIC_KEY}&hash=${hash}`)
    .then(response => response.json())
    .then(data => this.setState({data: data.data.results}));
    }

    hundleClick2=()=>{
      this.setState({
      limit : 8 , offset : 8
      })
      fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=${this.state.offset}&limit=${this.state.limit}&apikey=${PUBLIC_KEY}&hash=${hash}`)
      .then(response => response.json())
      .then(data => this.setState({data: data.data.results}));
      }

      hundleClick3=()=>{
        this.setState({
        limit : 8 , offset : 16
        })
        fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=${this.state.offset}&limit=${this.state.limit}&apikey=${PUBLIC_KEY}&hash=${hash}`)
        .then(response => response.json())
        .then(data => this.setState({data: data.data.results}));
        }



  render() {
    // this.props.onNbPageClick(this.state.data , this.state.limit, this.state.offset)
    console.log(this.state.data)


    const dataList = this.state.data.map((data,i)=>{
     return (<Comic key={i} 
      comicsTitle={data.name} 
      comicsUrl={data.urls[1].url} 
      comicsDesc={data.description} 
      comicsImg={`${data.thumbnail.path}/portrait_xlarge.jpg`}/>)
    })

    return (
      // <Provider store={store}>
    <div>

     <div style={{marginBottom: 90}}>
      <Navbar color="dark" dark expand="md" fixed="top">
        <span className="navbar-brand">
          <img src="./logo.png" width="30" height="30" className="d-inline-block align-top"/>
        </span>
        <NavbarToggler onClick={this.toggleNavBar} />
        <Collapse isOpen={this.state.isOpenNavBar} navbar>
          <Nav className="" navbar>
            <NavItem>
              <NavLink onClick={this.handleClickLikeOff}style={{color: "#FFFFFF"}}>Comic</NavLink>
            </NavItem>
            <NavItem>
              <NavLink onClick={this.handleClickLikeOn}style={{color: "#FFFFFF", marginRight: 50}}>Favoris</NavLink>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
      </div>

      <Container>
      <img className="marvel"src="./marvel_1.jpg" />
      <Row > 
        {dataList}
      </Row>
      <Button onClick={this.hundleClick}>1</Button>
      <Button onClick={this.hundleClick2}>2</Button>
      <Button onClick={this.hundleClick3}>3</Button>
      </Container>
    </div>

    // {/* </Provider> */}

    )
  }
}

