import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import CryptoJS from "crypto-js";
import Paginate from './Components/paginate'
import Comic from './Components/comic';
import {Container,Row} from 'reactstrap'
import {connect} from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
  state = {
    data: [],
    nbPage : [],
  }

  componentDidMount() {
    const PRIV_KEY = "ce1693d8baa06f879abcab4c75c19e34006074db";
    const PUBLIC_KEY = "1415ee478404a80e6f620f034e526ca0";
    const ts = new Date().getTime();


    const hash = CryptoJS.MD5(ts + PRIV_KEY + PUBLIC_KEY).toString();
    const limit = 20;

    const url = `http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=10&limit=${limit}&apikey=${PUBLIC_KEY}&hash=${hash}`
    console.log(ts)
    console.log(url)
    console.log()

    fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=0&limit=${limit}&apikey=${PUBLIC_KEY}&hash=${hash}`) 
    .then(response => response.json())
    .then(data => this.setState({data: data.data.results, nbPage: data.data.total / limit}));
  }
  render() {
    console.log(this.state.data)
    console.log("tessssssstttttt===>",this.state.nbPage )
    const dataList = this.state.data.map((data,i)=>{
     return (<Comic key={i} comicsTitle={data.name} comicsUrl={data.urls[1].url} comicsDesc={data.description} comicsImg={`${data.thumbnail.path}/standard_xlarge.jpg`}/>)
    })

    for(var i=0; i<this.state.nbPage;i++) {

    }

    return (
    <Container>
    <img className="marvel"src="./Logo-Marvel.png" />
    <Row> 
      {dataList}
      <Paginate/>
    </Row>
  </Container>

    )
  }
}

