import React, { Component } from 'react';
import {
  Col,
  Card,
  CardImg,
  CardBody,
  Button,
  CardTitle,
  Modal ,
  ModalHeader,
  ModalBody
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class Comic extends Component {
  constructor(props){
    super(props)
    this.state = {
      modal: false,
      url :false
    }
  }
  ModalVisible =()=>{
    console.log('Openmodal')
    this.setState({modal :true})
  }

  ModalInvisible =()=>{
    console.log('Closemodal')
    this.setState({modal :false})
  }

  HundleClick =()=>{
    console.log('testURL')
    this.setState({url : true})
  }

  render() {

    return (
      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30}}>
        <Card>
          <CardImg top width="100%" src={`http://i.annihil.us/u/prod/marvel/i/mg/7/30/${this.props.comicsImg}`} alt="Card image cap" style={{minHeight: 380}} />
          {/* <FontAwesomeIcon onClick={this.handleClick} size="2x" style={styleHeart}/> */}
            <CardBody style={{height: 150}}>
              <CardTitle>{this.props.comicsTitle}</CardTitle>
            </CardBody>
            <Button color="danger" onClick={this.ModalVisible}>Information</Button>
            <a href="#"onClick ={this.HundleClick}/>test
            <a href="#" onClick={this.HundleClick}>
            Click me
            </a>
        </Card>

        <Modal isOpen={this.state.modal} 
        ModalVisible={this.state.modal} >
        <ModalHeader ModalVisible={this.ModalVisible}>{this.props.comicsTitle}</ModalHeader>
          <CardImg top width="100%" src={`http://i.annihil.us/u/prod/marvel/i/mg/7/30/${this.props.comicsImg}`} alt="Card image cap" style={{minHeight: 380}} />
          <ModalBody>
            {this.props.comicsDesc}
          </ModalBody>
          <Button color='danger'onClick={this.ModalInvisible}>Close</Button>

        </Modal>
      </div>
    </Col>
    );
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default Comic;
