import React, { Component } from 'react';
import {
  Col,
  Card,
  CardImg,
  CardBody,
  Button,
  CardTitle,
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



class Comic extends Component {
  constructor(props){
    super(props)
    this.state = {
      modal: false
    }
  }
  ModalVisible =()=>{
    this.setState({modal :true})
  }

  ModalInvisible =()=>{
    this.setState({modal :false})
  }
  render() {

    return (
      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30}}>
        <Card>
          <CardImg top width="100%" src={`http://i.annihil.us/u/prod/marvel/i/mg/7/30/${this.props.comicsImg}`} alt="Card image cap" style={{minHeight: 380}} />
          {/* <FontAwesomeIcon onClick={this.handleClick} size="2x" style={styleHeart}/> */}
            <CardBody style={{height: 150}}>
              <CardTitle>{this.props.comicsTitle}</CardTitle>
            </CardBody>
          </Card>
      </div>
    </Col>
    );
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default Comic;
