import React, { Component } from 'react';
import {
  Col,
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import CryptoJS from "crypto-js";

class Comic extends Component {
  state = {
    data: []
  }

  componentDidMount() {
    const PRIV_KEY = "ce1693d8baa06f879abcab4c75c19e34006074db";
    const PUBLIC_KEY = "1415ee478404a80e6f620f034e526ca0";
    const ts = new Date().getTime();
    const hash = CryptoJS.MD5(ts + PRIV_KEY + PUBLIC_KEY).toString();
    fetch(`http://gateway.marvel.com/v1/public/comics?ts=${ts}&apikey=${PUBLIC_KEY}&hash=${hash}`) 
    .then(response => response.json())
    .then(data => this.setState({ data }));
  }
  render() {
    console.log(this.state.data)
    return (

      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30}}>
        <Card>
          <CardImg top width="100%" src={`https://image.tmdb.org/t/p/w500/${this.props.movieImg}`} alt="Card image cap" style={{minHeight: 380}} />
          {/* <FontAwesomeIcon onClick={this.handleClick} size="2x" style={styleHeart}/> */}
            <CardBody style={{height: 250}}>
              <CardTitle>Comictest</CardTitle>
              <CardText>Comictest2</CardText>
            </CardBody>
          </Card>
      </div>
    </Col>
    );
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default Comic;
