import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import CryptoJS from "crypto-js";
import Comic from './comic';
import {Container,Row} from 'reactstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
  state = {
    data: []
  }

  componentDidMount() {
    const PRIV_KEY = "ce1693d8baa06f879abcab4c75c19e34006074db";
    const PUBLIC_KEY = "1415ee478404a80e6f620f034e526ca0";
    const ts = new Date().getTime();
    const hash = CryptoJS.MD5(ts + PRIV_KEY + PUBLIC_KEY).toString();
    fetch(`http://gateway.marvel.com/v1/public/comics?ts=${ts}&apikey=${PUBLIC_KEY}&hash=${hash}`) 
    .then(response => response.json())
    .then(data => this.setState({data: data.data.results}));
  }
  render() {
    console.log(this.state.data)
    var dataList = this.state.data.map((data,i)=>{
     return (<Comic key={i} comicsTitle={data.title}  comicsImg={`https://image.tmdb.org/t/p/w500/${data.images.path}`}/>)
    })
    return (
      <Container>
      <Row>
      {dataList} 
      </Row>
    </Container>
    )
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default App;
