import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import CryptoJS from "crypto-js";
import Paginate from './Components/paginate'
import Comic from './Components/comic';
import {Container,Row,Pagination, PaginationItem, PaginationLink} from 'reactstrap'
import {connect} from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';



class App extends Component {
  state = {
    data: [],
    nbPage : [],
  }

  componentDidMount() {
    const PRIV_KEY = "ce1693d8baa06f879abcab4c75c19e34006074db";
    const PUBLIC_KEY = "1415ee478404a80e6f620f034e526ca0";
    const ts = new Date().getTime();


    const hash = CryptoJS.MD5(ts + PRIV_KEY + PUBLIC_KEY).toString();
    const limit = 24;
    const offset = 8

    const url = `http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=24&limit=${limit}&apikey=${PUBLIC_KEY}&hash=${hash}`
    console.log(ts)
    console.log(url)


    fetch(`http://gateway.marvel.com/v1/public/characters?ts=${ts}&offset=${offset}&limit=${limit}&apikey=${PUBLIC_KEY}&hash=${hash}`) 
    .then(response => response.json())
    .then(data => this.setState({data: data.data.results, nbPage: data.data.total}));
  }
  render() {
    console.log(this.state.data)
    console.log("tessssssstttttt===>",this.state.nbPage )
    const dataList = this.state.data.map((data,i)=>{
     return (<Comic key={i} comicsTitle={data.name} comicsUrl={data.urls[1].url} comicsDesc={data.description} comicsImg={`${data.thumbnail.path}/standard_xlarge.jpg`}/>)
    })

    for(var i=0; i<this.state.nbPage;i++) {

    }

    return (
    <Container>
    <img className="marvel"src="./Logo-Marvel.png" />
    <Row> 
      {dataList}
      <Pagination aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink onClick={"http://gateway.marvel.com/v1/public/characters?ts=1553252528936&offset=0&limit=8&apikey=1415ee478404a80e6f620f034e526ca0&hash=7c500b14e39985175e879c9690073d45"}>1</PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={"http://gateway.marvel.com/v1/public/characters?ts=1553252528936&offset=8&limit=16&apikey=1415ee478404a80e6f620f034e526ca0&hash=7c500b14e39985175e879c9690073d45"}>2</PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={"http://gateway.marvel.com/v1/public/characters?ts=1553252528936&offset=16&limit=24&apikey=1415ee478404a80e6f620f034e526ca0&hash=7c500b14e39985175e879c9690073d45"}>3</PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={"http://gateway.marvel.com/v1/public/characters?ts=1553252528936&offset=24&limit=32&apikey=1415ee478404a80e6f620f034e526ca0&hash=7c500b14e39985175e879c9690073d45"}>4</PaginationLink>
        </PaginationItem>
      </Pagination>
    </Row>
  </Container>

    )
  }
}

