import React, { Component } from 'react';
import {
  Col,
  Card,
  CardImg,
  CardBody,
  Button,
  CardTitle,
  Modal ,
  ModalHeader,
  ModalBody
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class Comic extends Component {
  constructor(props){
    super(props)
    this.state = {
      modal: false
    }
  }
  ModalVisible =()=>{
    console.log('test')
    this.setState({modal :true})
  }

  ModalInvisible =()=>{
    this.setState({modal :false})
  }
  render() {

    return (
      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30}}>
        <Card>
          <CardImg top width="100%" src={`http://i.annihil.us/u/prod/marvel/i/mg/7/30/${this.props.comicsImg}`} alt="Card image cap" style={{minHeight: 380}} />
          {/* <FontAwesomeIcon onClick={this.handleClick} size="2x" style={styleHeart}/> */}
            <CardBody style={{height: 150}}>
              <CardTitle>{this.props.comicsTitle}</CardTitle>
            </CardBody>
            <Button color="danger" onClick={this.ModalVisible}>Plus information</Button>
        </Card>
          <Modal isOpen={this.state.modal} 
        ModalVisible={this.state.modal} >
        <ModalHeader ModalVisible={this.ModalVisible}>{this.props.comicsTitle}</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <Button
            title="Go back"
            style={{width:200}}
            backgroundColor='#022F40'
            color='#FFFFFF'
            onPress={this.ModalInvisible}/>
        </Modal>
      </div>
    </Col>
    );
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default Comic;
