import React, { Component } from 'react';
import {
  Col,
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';


class Comic extends Component {
    constructor() {
    super();
        this.state = {

        };
      }

      componentDidMount(){
        fetch('http://localhost:3001/marvel')
        .then(function(response,) {
          return response.json();
        })
        .then(function(data) {
        console.log("MES COMICS",data)
        })
        .catch(function(error) {
          console.log('Request failed', error)
        });
      }

  render() {

    return (
      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30}}>
        <Card>
          <CardImg top width="100%" src={`https://image.tmdb.org/t/p/w500/${this.props.movieImg}`} alt="Card image cap" style={{minHeight: 380}} />
          <FontAwesomeIcon onClick={this.handleClick} size="2x" style={styleHeart}/>
            <CardBody style={{height: 250}}>
              <CardTitle>Comictest</CardTitle>
              <CardText>Comictest2</CardText>
            </CardBody>
          </Card>
      </div>
    </Col>
    );
  }
}

var styleHeart = {
  color: '#fc6861',
  position: 'absolute',
  top: '5%',
  left: '80%',
  cursor: 'pointer'
}

export default Comic;
